INSERT INTO `tbl_administrativo` (`administrativoId`, `puestoId`, `nombre`, `descripcion`, `imagen`, `status`, `createdBy`, `createdAt`, `updatedBy`, `updatedAt`) VALUES
(1, 1, 'Daniela Hernández Barrios', 'Daniela Hernández Barrios, Jefa del departamento de Sistemas y Computación de la carrera Ingeniería en Sistemas Computacionales en el Tecnológico de Veracruz; Ingeniero en Sistemas Computacionales, En el cargo de jefatura del departamento, su función principal, es coordinar la aplicación de programas de estudio relacionados con las áreas de sistemas y computación de las carreras que se imparten en el instituto tecnológico, así mismo el desarrollo de proyectos de investigación y vinculación con el sector productivo, derivados de los programas mencionados, proponiendo objetivos, metas y acciones; verificar que las actividades de las áreas se realicen de acuerdo con las normas, lineamientos y procedimientos establecidos con calidad y espíritu de servicio.', 'danielaHernandezBarrios.webp', 1, NULL, '2023-09-22', NULL, '2023-09-22'),
(2, 1, 'Claudia Guadalupe Zarrabal Gutíerrez', 'Claudia Guadalupe Zarrabal Gutíerrez, Jefa del Departamento de Ciencias Económico-Administrativas en el Tecnológico de Veracruz; Docente de Ciencias Económico Administrativas con experiencia en proyectos de innovación y mercadotenia. Como jefa del departamento tiene la función de coordinar la aplicación de los programas de estudio de Licenciatura en Administración, Ingeniería en Gestión Empresarial y posgrados ', 'claudiaGuadalupeZarrabalGutierrez.png', 1, NULL, '2023-09-22', NULL, '2023-09-22'),
(3, 1, 'Ángel Miranda Juarez', 'Ángel Miranda Juarez, Jefe del departamento de Metal-Mecánica en el Tecnológico de Veracruz, tiene la responsabilidad de coordinar programas de estudio relacionados con Ingeniería Mecatrónica. Su función principal es supervisar la aplicación de programas de estudio y proyectos de investigación, así como la vinculación con el sector productivo. Esto incluye proponer objetivos, metas y acciones. Además, debe garantizar que las actividades del departamento cumplan con las normas y procedimientos establecidos, manteniendo altos estándares de calidad y servicio.', 'angelMirandaJuarez.webp', 1, NULL, '2023-09-22', NULL, '2023-09-22'),
(4, 1, 'Carmen Yolanda Hernández Cardona', 'Carmen Yolanda Hernández Cardona, Ingeniera Industrial en Química y Maestra en Ciencias en Bioingeniería. Como jefa del departamento tiene la función de coordinar la aplicación de los programas de estudio de Ingeniería Química, Ingeniería Bioquímica y posgrados relacionados que se imparten en el Instituto Tecnológico, así como el desarrollo de proyectos de investigación y vinculación con el sector productivo derivados de los programas mencionados.', 'yolandaHernandezCardona.webp', 1, NULL, '2023-09-22', NULL, '2023-09-22'),
(5, 1, 'María Elena Maceda Rodríguez', 'María Elena Maceda Rodríguez, Ingeniera Industrial egresada del TecNM campus Veracruz, con Maestría en Ingeniería Industrial por parte del TecNM campus Orizaba y una especialización en Comercio Exterior por parte de la Universidad Veracruzana. En el cargo de jefatura del departamento, su función principal, es coordinar la aplicación de programas de estudio relacionados con las áreas de ingeniería industrial de las carreras que se imparten en el instituto tecnológico, así mismo el desarrollo de proyectos de investigación y vinculación con el sector productivo, derivados de los programas mencionados, proponiendo objetivos, metas y acciones; verificar que las actividades de las áreas se realicen de acuerdo con las normas, lineamientos y procedimientos establecidos con calidad y espíritu de servicio.', 'mariaElenaMacedaRodriguez.webp', 1, NULL, '2023-09-22', NULL, '2023-09-22'),
(6, 1, 'Leodegario Vásquez González', NULL, 'leodegarioVasquezGonzalez.webp', 1, NULL, '2023-09-22', NULL, '2023-09-22'),
(7, 5, 'Ivonne Ramirez Gutíerrez', 'Ivonne Ramírez Gutiérrez, coordinadora de las carreras Ingeniería en Gestión Empresarial y Lic. en Administración en el Tecnológico de Veracruz; licenciada en Administración con maestría en Educación. En el cargo de coordinación, su función principal, es la orientación académica de los alumnos estudiantes de dichas licenciaturas a lo largo de su estancia en la Institución, para un correcto avance y aprovechamiento de su programa de estudios. Así mismo se realizan diferentes funciones en la coordinación, como lo son: consulta y cambio de NIP, realizar la estructura académica con base en las estadísticas de la población inscrita y las necesidades de grupos que así se generen dentro de los periodos semestrales así como de cursos de verano.', 'ivonneRamirezGuetierrez.png', 1, NULL, '2023-09-22', NULL, '2023-09-22'),
(8, 5, 'Francisca Patricia Díaz García', 'Francisca Patricia Díaz García, Licenciada en Psicología Educativa. En el cargo de coordinación, tiene como función principal dar seguimiento al estudiante durante su estancia en la institución para ofrecer la orientación necesaria para un correcto avance y aprovechamiento de su plan de estudios. Así mismo se realizan diferentes funciones tales como definir semestralmente la apertura del número de grupos por asignatura con base a la demanda estudiantil, consulta y cambio de NIP, análisis de casos extraordinarios, entre otros.', 'patriciaDiazGarcia.webp', 1, NULL, '2023-09-22', NULL, '2023-09-22'),
